#
# TABLE STRUCTURE FOR: pejabat
#

DROP TABLE IF EXISTS `pejabat`;

CREATE TABLE `pejabat` (
  `id_pejabat` char(8) NOT NULL,
  `nama_pejabat` varchar(100) NOT NULL,
  `jabatan` varchar(100) NOT NULL,
  `golongan` varchar(50) NOT NULL,
  `nip` varchar(40) NOT NULL,
  PRIMARY KEY (`id_pejabat`),
  KEY `id_pejabat` (`id_pejabat`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: penelitian
#

DROP TABLE IF EXISTS `penelitian`;

CREATE TABLE `penelitian` (
  `id_penelitian` int(11) NOT NULL AUTO_INCREMENT,
  `waktu_pembuatan` date NOT NULL,
  `jenis_surat` varchar(100) NOT NULL,
  `maksud` varchar(500) NOT NULL,
  `waktu_mulai` date NOT NULL,
  `waktu_selesai` date NOT NULL,
  `no_bkbpm` varchar(100) NOT NULL,
  `tanggal_bkbpm` date NOT NULL,
  `surat` varchar(300) NOT NULL,
  `no_surat` varchar(100) NOT NULL,
  `tanggal_surat` date NOT NULL,
  `tembusan` varchar(100) NOT NULL,
  `id_pejabat` char(8) NOT NULL,
  `id_pengaju` int(11) NOT NULL,
  PRIMARY KEY (`id_penelitian`),
  KEY `id_pejabat_2` (`id_pejabat`),
  KEY `id_pengaju` (`id_pengaju`),
  CONSTRAINT `penelitian_ibfk_1` FOREIGN KEY (`id_pejabat`) REFERENCES `pejabat` (`id_pejabat`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `penelitian_ibfk_2` FOREIGN KEY (`id_pengaju`) REFERENCES `pengaju` (`id_pengaju`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=75 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: pengaju
#

DROP TABLE IF EXISTS `pengaju`;

CREATE TABLE `pengaju` (
  `id_pengaju` int(11) NOT NULL AUTO_INCREMENT,
  `nama` varchar(200) NOT NULL,
  `alamat` varchar(200) NOT NULL,
  `institusi` varchar(200) NOT NULL,
  PRIMARY KEY (`id_pengaju`),
  KEY `id_pengaju` (`id_pengaju`)
) ENGINE=InnoDB AUTO_INCREMENT=78 DEFAULT CHARSET=latin1;



#
# TABLE STRUCTURE FOR: pengajuan
#

DROP TABLE IF EXISTS `pengajuan`;

CREATE TABLE `pengajuan` (
  `id_pengajuan` int(11) NOT NULL,
  `id_penelitian` int(11) NOT NULL,
  `id_pengaju` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

#
# TABLE STRUCTURE FOR: rel_tembusan
#

DROP TABLE IF EXISTS `rel_tembusan`;

CREATE TABLE `rel_tembusan` (
  `id_penelitian` int(11) NOT NULL,
  `id_tembusan` char(4) NOT NULL,
  KEY `id_penelitian` (`id_penelitian`),
  KEY `id_tembusan` (`id_tembusan`),
  CONSTRAINT `rel_tembusan_ibfk_1` FOREIGN KEY (`id_tembusan`) REFERENCES `tembusan` (`id_tembusan`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `rel_tembusan_ibfk_2` FOREIGN KEY (`id_penelitian`) REFERENCES `penelitian` (`id_penelitian`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

#
# TABLE STRUCTURE FOR: tembusan
#

DROP TABLE IF EXISTS `tembusan`;

CREATE TABLE `tembusan` (
  `id_tembusan` char(4) NOT NULL,
  `nama` varchar(150) NOT NULL,
  PRIMARY KEY (`id_tembusan`),
  KEY `id_tembusan` (`id_tembusan`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;



